CREATE DATABASE assignment;
USE assignment;

CREATE TABLE airlines_info(airline_id INT,airline_name VARCHAR(100),country VARCHAR(50),fleet_size INT,founded_year INT);
CREATE TABLE country_info(country_id INT,country_name VARCHAR(100),continent VARCHAR(50),population BIGINT,official_language VARCHAR(50));
CREATE TABLE state_info (state_id INT,state_name VARCHAR(100),country_id INT,population BIGINT,capital_city VARCHAR(100));

ALTER TABLE airlines_info ADD COLUMN headquarters VARCHAR(100),ADD COLUMN ceo_name VARCHAR(100),ADD COLUMN website VARCHAR(100);
ALTER TABLE country_info ADD COLUMN currency VARCHAR(50),ADD COLUMN gdp_trillion DECIMAL(5,2),ADD COLUMN area_sq_km BIGINT;
ALTER TABLE state_info ADD COLUMN area_sq_km BIGINT,ADD COLUMN governor_name VARCHAR(100),ADD COLUMN abbreviation CHAR(2);

ALTER TABLE airlines_info CHANGE fleet_size total_fleet INT,CHANGE founded_year established_year INT,CHANGE ceo_name chief_executive_officer VARCHAR(100);
ALTER TABLE country_info CHANGE population total_population BIGINT,CHANGE currency national_currency VARCHAR(50),CHANGE area_sq_km total_area BIGINT;
ALTER TABLE state_info CHANGE population total_population BIGINT,CHANGE abbreviation state_code CHAR(2),CHANGE area_sq_km total_area BIGINT;

ALTER TABLE airlines_info MODIFY total_fleet SMALLINT,MODIFY headquarters TEXT;
ALTER TABLE country_info MODIFY gdp_trillion FLOAT,MODIFY national_currency VARCHAR(100),MODIFY total_population INT;
ALTER TABLE state_info MODIFY state_code VARCHAR(5),MODIFY total_area FLOAT,MODIFY governor_name TEXT;

